import React,{useState} from 'react'
import './assets/font.css'
import { Row, Col } from 'antd';
import img from './assets/img.jpg'
import Form from './Form'
import axios from 'axios'
import { useMediaQuery } from 'react-responsive'


export default function Content() {
  const isMobile = useMediaQuery({ query: '(max-width: 780px)' })
  const [data, setData] = useState([]);
  const [status, setStatus] = useState(false);
  const getInfo = async() => { 
    const result = await axios.get(`https://panjs.com/ywc18.json`
);
    setData(result.data);

if (result.status === 200) {
    setStatus(true)
      }

console.log(data)
}
  return (

      <div className="site-layout-background" style={{ padding: 8, minHeight: 2080 }}>
      <div style={{ color: "black", fontWeight: 600, fontSize: '24px' }}>ผลการค้นหา ทั้งหมด</div>
      <button onClick={getInfo}>check</button>

          <Row gutter={{ xs: 8, sm: 16, md: 24, lg: 32 }}>
            {!isMobile &&
             <Col span={6} >
             <Form/>
                      </Col>
            } 
            {!isMobile  && 
                <Col span={18}  >
                <div style={{ background: "white", padding: 10, width: "100%" }}>
                  <Row gutter={{ xs: 12, sm: 8, md: 24, lg: 2 }}>

                    <img src={img} alt="img" width="240px" height="240" />


                  <Col offset={1}>


                  <div style={{fontSize:'1.06rem',fontWeight:500}}>
                      ชื่อร้านค้า
             </div>
             <div style={{fontSize:'1.06rem',fontWeight:300,color:'grey'}}>
            sub | subset
             </div>


                    </Col>

                  </Row>
                </div>
              </Col>


            }

  </Row>
          {isMobile &&
            <div style={{ background: "white", width: "100%" ,display:"block"}}> 
            <img src={img} alt="img" width="100%"  />
                tracking
              </div>
           }
      </div>

  );
}
