import React,{useState} from 'react'
import './assets/font.css'
import { Row, Col,Divider } from 'antd';
import img from './assets/img.jpg'
import Form from './Form'
import axios from 'axios'
import { useMediaQuery } from 'react-responsive'


export default function Content() {
  const isMobile = useMediaQuery({ query: '(max-width: 780px)' })
  const [price, setPrice] = useState('');
  const [provice, setProvice] = useState('');
  const [value, setValue] = useState(0)
  const name = ['ทั้งหมด', 'ร้านอาหาร และเครื่องดื่ม', 'ร้านค้า OTOP', 'ร้านค้าธงฟ้า', 'สินค้าทั่วไป']
  const [data, setData] = useState([]);
  React.useEffect(() => {
    const fetchData = async () => {
      const result = await axios.get(
        "https://panjs.com/ywc18.json",
      );
      setData(result.data.merchants);
    };

    fetchData();
  }, [value]);
  const getnName = () => {
    return name[value]
  }
  const type = (data, value, price, provice) => {
    if (provice === "" || provice === "กรุงเทพมหานคร") {
      if ((value === 4 && data.categoryName === 'งานบริการอื่นๆ / เบ็ดเตล็ด') || (value === 4 && data.categoryName === 'แฟชั่น')) {
        if (price === '') {
          return (

            <div style={{ background: "white", padding: 10, width: "100%", marginBottom: 10 }}>
              <Row gutter={{ xs: 12, sm: 8, md: 24, lg: 2 }}>

                <img src={data.coverImageId} alt="img" width="240px" height="240" />


                <Col offset={1}>

                  <div style={{ display: 'inline-flex' }}>
                    <div style={{ fontSize: '1.06rem', fontWeight: 500 }}>
                      {data.shopNameTH}

                    </div>
                    <div style={{ fontSize: '0.8rem', fontWeight: 300, background: 'green', color: 'white', marginLeft: 8, padding: '4px 10px 4px 10px', borderRadius: 10 }}>
                      {data.isOpen === "Y" ? "เปิด" : data.isOpen}
                    </div>
                  </div>
                  <div style={{ fontSize: '1.06rem', fontWeight: 300, color: 'grey' }}>
                    {data.categoryName}
                  </div>


                  <div style={{ fontSize: '1rem', fontWeight: 300 }}>
                    {data.subcategoryName} | ช่วงราคา    {data.priceLevel} |      {data.addressProvinceName} | {data.addressDistrictName}
                  </div>
                  <Divider />
                  <div style={{ fontSize: '1rem', fontWeight: 300 }}>
                    {data.facilities}
                  </div>




                  <div style={{ fontSize: '1rem', fontWeight: 300 }}>
                    <div dangerouslySetInnerHTML={{ __html: data.highlightText }}></div>

                  </div>
                  <div style={{ display: 'inline-flex', alignItems: 'center' }}>
                    <div style={{ fontSize: '1rem', fontWeight: 500 }}>
                      เมนูแนะนำ:
              </div>
                    <div style={{ fontSize: '1rem', fontWeight: 300, color: 'gray' }}>
                      {data.recommendedItems}
                    </div>

                  </div>


                </Col>

              </Row>

            </div>





          )

        } if (price === data.priceLevel) {
          return (
            <div style={{ background: "white", padding: 10, width: "100%", marginBottom: 10 }}>
              <Row gutter={{ xs: 12, sm: 8, md: 24, lg: 2 }}>

                <img src={data.coverImageId} alt="img" width="240px" height="240" />


                <Col offset={1}>

                  <div style={{ display: 'inline-flex' }}>
                    <div style={{ fontSize: '1.06rem', fontWeight: 500 }}>
                      {data.shopNameTH}

                    </div>
                    <div style={{ fontSize: '0.8rem', fontWeight: 300, background: 'green', color: 'white', marginLeft: 8, padding: '4px 10px 4px 10px', borderRadius: 10 }}>
                      {data.isOpen === "Y" ? "เปิด" : data.isOpen}
                    </div>
                  </div>
                  <div style={{ fontSize: '1.06rem', fontWeight: 300, color: 'grey' }}>
                    {data.categoryName}
                  </div>


                  <div style={{ fontSize: '1rem', fontWeight: 300 }}>
                    {data.subcategoryName} | ช่วงราคา    {data.priceLevel} |      {data.addressProvinceName} | {data.addressDistrictName}
                  </div>
                  <Divider />
                  <div style={{ fontSize: '1rem', fontWeight: 300 }}>
                    {data.facilities}
                  </div>




                  <div style={{ fontSize: '1rem', fontWeight: 300 }}>
                    <div dangerouslySetInnerHTML={{ __html: data.highlightText }}></div>

                  </div>
                  <div style={{ display: 'inline-flex', alignItems: 'center' }}>
                    <div style={{ fontSize: '1rem', fontWeight: 500 }}>
                      เมนูแนะนำ:
            </div>
                    <div style={{ fontSize: '1rem', fontWeight: 300, color: 'gray' }}>
                      {data.recommendedItems}
                    </div>

                  </div>


                </Col>

              </Row>

            </div>
          )
        }



      }


      if (value === 1 && data.categoryName === 'ร้านอาหาร') {

        if (price === '') {
          return (

            <div style={{ background: "white", padding: 10, width: "100%", marginBottom: 10 }}>
              <Row gutter={{ xs: 12, sm: 8, md: 24, lg: 2 }}>

                <img src={data.coverImageId} alt="img" width="240px" height="240" />


                <Col offset={1}>

                  <div style={{ display: 'inline-flex' }}>
                    <div style={{ fontSize: '1.06rem', fontWeight: 500 }}>
                      {data.shopNameTH}

                    </div>
                    <div style={{ fontSize: '0.8rem', fontWeight: 300, background: 'green', color: 'white', marginLeft: 8, padding: '4px 10px 4px 10px', borderRadius: 10 }}>
                      {data.isOpen === "Y" ? "เปิด" : data.isOpen}
                    </div>
                  </div>
                  <div style={{ fontSize: '1.06rem', fontWeight: 300, color: 'grey' }}>
                    {data.categoryName}
                  </div>


                  <div style={{ fontSize: '1rem', fontWeight: 300 }}>
                    {data.subcategoryName} | ช่วงราคา    {data.priceLevel} |      {data.addressProvinceName} | {data.addressDistrictName}
                  </div>
                  <Divider />
                  <div style={{ fontSize: '1rem', fontWeight: 300 }}>
                    {data.facilities}
                  </div>




                  <div style={{ fontSize: '1rem', fontWeight: 300 }}>
                    <div dangerouslySetInnerHTML={{ __html: data.highlightText }}></div>

                  </div>
                  <div style={{ display: 'inline-flex', alignItems: 'center' }}>
                    <div style={{ fontSize: '1rem', fontWeight: 500 }}>
                      เมนูแนะนำ:
                </div>
                    <div style={{ fontSize: '1rem', fontWeight: 300, color: 'gray' }}>
                      {data.recommendedItems}
                    </div>

                  </div>


                </Col>

              </Row>

            </div>





          )

        } if (price === data.priceLevel) {
          return (
            <div style={{ background: "white", padding: 10, width: "100%", marginBottom: 10 }}>
              <Row gutter={{ xs: 12, sm: 8, md: 24, lg: 2 }}>

                <img src={data.coverImageId} alt="img" width="240px" height="240" />


                <Col offset={1}>

                  <div style={{ display: 'inline-flex' }}>
                    <div style={{ fontSize: '1.06rem', fontWeight: 500 }}>
                      {data.shopNameTH}

                    </div>
                    <div style={{ fontSize: '0.8rem', fontWeight: 300, background: 'green', color: 'white', marginLeft: 8, padding: '4px 10px 4px 10px', borderRadius: 10 }}>
                      {data.isOpen === "Y" ? "เปิด" : data.isOpen}
                    </div>
                  </div>
                  <div style={{ fontSize: '1.06rem', fontWeight: 300, color: 'grey' }}>
                    {data.categoryName}
                  </div>


                  <div style={{ fontSize: '1rem', fontWeight: 300 }}>
                    {data.subcategoryName} | ช่วงราคา    {data.priceLevel} |      {data.addressProvinceName} | {data.addressDistrictName}
                  </div>
                  <Divider />
                  <div style={{ fontSize: '1rem', fontWeight: 300 }}>
                    {data.facilities}
                  </div>




                  <div style={{ fontSize: '1rem', fontWeight: 300 }}>
                    <div dangerouslySetInnerHTML={{ __html: data.highlightText }}></div>

                  </div>
                  <div style={{ display: 'inline-flex', alignItems: 'center' }}>
                    <div style={{ fontSize: '1rem', fontWeight: 500 }}>
                      เมนูแนะนำ:
              </div>
                    <div style={{ fontSize: '1rem', fontWeight: 300, color: 'gray' }}>
                      {data.recommendedItems}
                    </div>

                  </div>


                </Col>

              </Row>

            </div>
          )
        }




      }
      if (value === 0) {
        if (price === '') {
          return (

            <div style={{ background: "white", padding: 10, width: "100%", marginBottom: 10 }}>
              <Row gutter={{ xs: 12, sm: 8, md: 24, lg: 2 }}>

                <img src={data.coverImageId} alt="img" width="240px" height="240" />


                <Col offset={1}>

                  <div style={{ display: 'inline-flex' }}>
                    <div style={{ fontSize: '1.06rem', fontWeight: 500 }}>
                      {data.shopNameTH}

                    </div>
                    <div style={{ fontSize: '0.8rem', fontWeight: 300, background: 'green', color: 'white', marginLeft: 8, padding: '4px 10px 4px 10px', borderRadius: 10 }}>
                      {data.isOpen === "Y" ? "เปิด" : data.isOpen}
                    </div>
                  </div>
                  <div style={{ fontSize: '1.06rem', fontWeight: 300, color: 'grey' }}>
                    {data.categoryName}
                  </div>


                  <div style={{ fontSize: '1rem', fontWeight: 300 }}>
                    {data.subcategoryName} |  {data.addressProvinceName} | {data.addressDistrictName}
                  </div>
                  <Divider />
                  <div style={{ fontSize: '1rem', fontWeight: 300 }}>
                    {data.facilities}
                  </div>




                  <div style={{ fontSize: '1rem', fontWeight: 300 }}>
                    <div dangerouslySetInnerHTML={{ __html: data.highlightText }}></div>

                  </div>
                  <div style={{ display: 'inline-flex', alignItems: 'center' }}>
                    <div style={{ fontSize: '1rem', fontWeight: 500 }}>
                      เมนูแนะนำ:
                </div>
                    <div style={{ fontSize: '1rem', fontWeight: 300, color: 'gray' }}>
                      {data.recommendedItems}
                    </div>

                  </div>


                </Col>

              </Row>

            </div>





          )

        } if (price === data.priceLevel) {
          return (
            <div style={{ background: "white", padding: 10, width: "100%", marginBottom: 10 }}>
              <Row gutter={{ xs: 12, sm: 8, md: 24, lg: 2 }}>

                <img src={data.coverImageId} alt="img" width="240px" height="240" />


                <Col offset={1}>

                  <div style={{ display: 'inline-flex' }}>
                    <div style={{ fontSize: '1.06rem', fontWeight: 500 }}>
                      {data.shopNameTH}

                    </div>
                    <div style={{ fontSize: '0.8rem', fontWeight: 300, background: 'green', color: 'white', marginLeft: 8, padding: '4px 10px 4px 10px', borderRadius: 10 }}>
                      {data.isOpen === "Y" ? "เปิด" : data.isOpen}
                    </div>
                  </div>
                  <div style={{ fontSize: '1.06rem', fontWeight: 300, color: 'grey' }}>
                    {data.categoryName}
                  </div>


                  <div style={{ fontSize: '1rem', fontWeight: 300 }}>
                    {data.subcategoryName} | ช่วงราคา    {data.priceLevel} |      {data.addressProvinceName} | {data.addressDistrictName}
                  </div>
                  <Divider />
                  <div style={{ fontSize: '1rem', fontWeight: 300 }}>
                    {data.facilities}
                  </div>




                  <div style={{ fontSize: '1rem', fontWeight: 300 }}>
                    <div dangerouslySetInnerHTML={{ __html: data.highlightText }}></div>

                  </div>
                  <div style={{ display: 'inline-flex', alignItems: 'center' }}>
                    <div style={{ fontSize: '1rem', fontWeight: 500 }}>
                      เมนูแนะนำ:
              </div>
                    <div style={{ fontSize: '1rem', fontWeight: 300, color: 'gray' }}>
                      {data.recommendedItems}
                    </div>

                  </div>


                </Col>

              </Row>

            </div>
          )
        }





      }
    }
  }

  return (

      <div className="site-layout-background" style={{ padding: 8, minHeight: 2080 }}>
      <div style={{ color: "black", fontWeight: 600, fontSize: '24px' }}>ผลการค้นหา { getnName()}</div>
        <>    
        <Row gutter={{ xs: 8, sm: 16, md: 24, lg: 32 }}>
            {!isMobile &&
             <Col span={6} >
            <Form value={value} setValue={setValue} price={price} setPrice={setPrice} provice={provice} setProvice={setProvice}/>
                      </Col>
            } 
            {!isMobile  && 
            <Col span={18} offset={0} >
            {data.map((data, index) =>

           type(data,value,price,provice)
            )}
              </Col> 
            }      
  </Row>
          {isMobile &&
            <div style={{ background: "white", width: "100%" ,display:"block"}}> 
            <img src={img} alt="img" width="100%"  />
            <div style={{fontSize:'1.06rem',fontWeight:500}}>
                  {data.shopNameTH}
             </div>
              </div>
           }
        </>







      </div>

  );
}
