import React,{useState} from 'react'
import './assets/font.css'
import { Row, Col,Divider } from 'antd';
import img from './assets/img.jpg'
import Form from './Form'
import axios from 'axios'
import { useMediaQuery } from 'react-responsive'


export default function Content({value,setValue,price,setPrice,provice, setProvice, subCategory, setSubCategory }) {
  const isMobile = useMediaQuery({ query: '(max-width: 1299px)' })
  const name = ['ทั้งหมด', 'ร้านอาหาร และเครื่องดื่ม', 'ร้านค้า OTOP', 'ร้านค้าธงฟ้า', 'สินค้าทั่วไป']
  const [data, setData] = useState([]);
  const [sub,setSub]=useState([]);
  React.useEffect(() => {
    const fetchData = async () => {
      const result = await axios.get(
        "https://panjs.com/ywc18.json",
      );
      setData(result.data.merchants);
      setSub(result.data.subcategoryName)
    };

    fetchData();
  }, [value]);
  const getnName = () => {
    return name[value]
  }
  const type = (data, value, price, provice) => {
    if (provice === "" || provice === "กรุงเทพมหานคร") {
      if ((value === 4 && data.categoryName === 'งานบริการอื่นๆ / เบ็ดเตล็ด') || (value === 4 && data.categoryName === 'แฟชั่น')) {
        if (price === '') {
          if (data.categoryName){}
          return (

            <div style={{ background: "white", padding: 10, width: "100%", marginBottom: 10 }}>
              <Row gutter={{ xs: 12, sm: 8, md: 24, lg: 2 }}>

                <img src={data.coverImageId} alt="img" width="240px" height="240" />


                <Col offset={1}>

                  <div style={{ display: 'inline-flex' }}>
                    <div style={{ fontSize: '1.06rem', fontWeight: 500 }}>
                      {data.shopNameTH}

                    </div>
                    <div style={{ fontSize: '0.8rem', fontWeight: 300, background: 'green', color: 'white', marginLeft: 8, padding: '4px 10px 4px 10px', borderRadius: 10 }}>
                      {data.isOpen === "Y" ? "เปิด" : data.isOpen}
                    </div>
                  </div>
                  <div style={{ fontSize: '1.06rem', fontWeight: 300, color: 'grey' }}>
                    {data.categoryName}
                  </div>


                  <div style={{ fontSize: '1rem', fontWeight: 300 }}>
                    {data.subcategoryName} | ช่วงราคา    {data.priceLevel} |      {data.addressProvinceName} | {data.addressDistrictName}
                  </div>
                  <Divider />
                  <div style={{ fontSize: '1rem', fontWeight: 300 }}>
                    {data.facilities}
                  </div>




                  <div style={{ fontSize: '1rem', fontWeight: 300 }}>
                    <div dangerouslySetInnerHTML={{ __html: data.highlightText }}></div>

                  </div>
                  <div style={{ display: 'inline-flex', alignItems: 'center' }}>
                    <div style={{ fontSize: '1rem', fontWeight: 500 }}>
                      เมนูแนะนำ:
              </div>
                    <div style={{ fontSize: '1rem', fontWeight: 300, color: 'gray' }}>
                      {data.recommendedItems}
                    </div>

                  </div>


                </Col>

              </Row>

            </div>





          )

        } if (price === data.priceLevel) {
          return (
            <div style={{ background: "white", padding: 10, width: "100%", marginBottom: 10 }}>
              <Row gutter={{ xs: 12, sm: 8, md: 24, lg: 2 }}>

                <img src={data.coverImageId} alt="img" width="240px" height="240" />


                <Col offset={1}>

                  <div style={{ display: 'inline-flex' }}>
                    <div style={{ fontSize: '1.06rem', fontWeight: 500 }}>
                      {data.shopNameTH}

                    </div>
                    <div style={{ fontSize: '0.8rem', fontWeight: 300, background: 'green', color: 'white', marginLeft: 8, padding: '4px 10px 4px 10px', borderRadius: 10 }}>
                      {data.isOpen === "Y" ? "เปิด" : data.isOpen}
                    </div>
                  </div>
                  <div style={{ fontSize: '1.06rem', fontWeight: 300, color: 'grey' }}>
                    {data.categoryName}
                  </div>


                  <div style={{ fontSize: '1rem', fontWeight: 300 }}>
                    {data.subcategoryName} | ช่วงราคา    {data.priceLevel} |      {data.addressProvinceName} | {data.addressDistrictName}
                  </div>
                  <Divider />
                  <div style={{ fontSize: '1rem', fontWeight: 300 }}>
                    {data.facilities}
                  </div>




                  <div style={{ fontSize: '1rem', fontWeight: 300 }}>
                    <div dangerouslySetInnerHTML={{ __html: data.highlightText }}></div>

                  </div>
                  <div style={{ display: 'inline-flex', alignItems: 'center' }}>
                    <div style={{ fontSize: '1rem', fontWeight: 500 }}>
                      เมนูแนะนำ:
            </div>
                    <div style={{ fontSize: '1rem', fontWeight: 300, color: 'gray' }}>
                      {data.recommendedItems}
                    </div>

                  </div>


                </Col>

              </Row>

            </div>
          )
        }



      }


      if (value === 1 && data.categoryName === 'ร้านอาหาร') {

        if (price === '') {
          return (

            <div style={{ background: "white", padding: 10, width: "100%", marginBottom: 10 }}>
              <Row gutter={{ xs: 12, sm: 8, md: 24, lg: 2 }}>

                <img src={data.coverImageId} alt="img" width="240px" height="240" />


                <Col offset={1}>

                  <div style={{ display: 'inline-flex' }}>
                    <div style={{ fontSize: '1.06rem', fontWeight: 500 }}>
                      {data.shopNameTH}

                    </div>
                    <div style={{ fontSize: '0.8rem', fontWeight: 300, background: 'green', color: 'white', marginLeft: 8, padding: '4px 10px 4px 10px', borderRadius: 10 }}>
                      {data.isOpen === "Y" ? "เปิด" : data.isOpen}
                    </div>
                  </div>
                  <div style={{ fontSize: '1.06rem', fontWeight: 300, color: 'grey' }}>
                    {data.categoryName}
                  </div>


                  <div style={{ fontSize: '1rem', fontWeight: 300 }}>
                    {data.subcategoryName} | ช่วงราคา    {data.priceLevel} |      {data.addressProvinceName} | {data.addressDistrictName}
                  </div>
                  <Divider />
                  <div style={{ fontSize: '1rem', fontWeight: 300 }}>
                    {data.facilities}
                  </div>




                  <div style={{ fontSize: '1rem', fontWeight: 300 }}>
                    <div dangerouslySetInnerHTML={{ __html: data.highlightText }}></div>

                  </div>
                  <div style={{ display: 'inline-flex', alignItems: 'center' }}>
                    <div style={{ fontSize: '1rem', fontWeight: 500 }}>
                      เมนูแนะนำ:
                </div>
                    <div style={{ fontSize: '1rem', fontWeight: 300, color: 'gray' }}>
                      {data.recommendedItems}
                    </div>

                  </div>


                </Col>

              </Row>

            </div>





          )

        } if (price === data.priceLevel) {
          return (
            <div style={{ background: "white", padding: 10, width: "100%", marginBottom: 10 }}>
              <Row gutter={{ xs: 12, sm: 8, md: 24, lg: 2 }}>

                <img src={data.coverImageId} alt="img" width="240px" height="240" />


                <Col offset={1}>

                  <div style={{ display: 'inline-flex' }}>
                    <div style={{ fontSize: '1.06rem', fontWeight: 500 }}>
                      {data.shopNameTH}

                    </div>
                    <div style={{ fontSize: '0.8rem', fontWeight: 300, background: 'green', color: 'white', marginLeft: 8, padding: '4px 10px 4px 10px', borderRadius: 10 }}>
                      {data.isOpen === "Y" ? "เปิด" : data.isOpen}
                    </div>
                  </div>
                  <div style={{ fontSize: '1.06rem', fontWeight: 300, color: 'grey' }}>
                    {data.categoryName}
                  </div>


                  <div style={{ fontSize: '1rem', fontWeight: 300 }}>
                    {data.subcategoryName} | ช่วงราคา    {data.priceLevel} |      {data.addressProvinceName} | {data.addressDistrictName}
                  </div>
                  <Divider />
                  <div style={{ fontSize: '1rem', fontWeight: 300 }}>
                    {data.facilities}
                  </div>




                  <div style={{ fontSize: '1rem', fontWeight: 300 }}>
                    <div dangerouslySetInnerHTML={{ __html: data.highlightText }}></div>

                  </div>
                  <div style={{ display: 'inline-flex', alignItems: 'center' }}>
                    <div style={{ fontSize: '1rem', fontWeight: 500 }}>
                      เมนูแนะนำ:
              </div>
                    <div style={{ fontSize: '1rem', fontWeight: 300, color: 'gray' }}>
                      {data.recommendedItems}
                    </div>

                  </div>


                </Col>

              </Row>

            </div>
          )
        }




      }
      if (value === 0) {
        if (price === '') {
          return (

            <div style={{ background: "white", padding: 10, width: "100%", marginBottom: 10 }}>
              <Row gutter={{ xs: 12, sm: 8, md: 24, lg: 2 }}>

                <img src={data.coverImageId} alt="img" width="240px" height="240" />


                <Col offset={1}>

                  <div style={{ display: 'inline-flex' }}>
                    <div style={{ fontSize: '1.06rem', fontWeight: 500 }}>
                      {data.shopNameTH}

                    </div>
                    <div style={{ fontSize: '0.8rem', fontWeight: 300, background: 'green', color: 'white', marginLeft: 8, padding: '4px 10px 4px 10px', borderRadius: 10 }}>
                      {data.isOpen === "Y" ? "เปิด" : data.isOpen}
                    </div>
                  </div>
                  <div style={{ fontSize: '1.06rem', fontWeight: 300, color: 'grey' }}>
                    {data.categoryName}
                  </div>


                  <div style={{ fontSize: '1rem', fontWeight: 300 }}>
                    {data.subcategoryName} |  {data.addressProvinceName} | {data.addressDistrictName}
                  </div>
                  <Divider />
                  <div style={{ fontSize: '1rem', fontWeight: 300 }}>
                    {data.facilities}
                  </div>




                  <div style={{ fontSize: '1rem', fontWeight: 300 }}>
                    <div dangerouslySetInnerHTML={{ __html: data.highlightText }}></div>

                  </div>
                  <div style={{ display: 'inline-flex', alignItems: 'center' }}>
                    <div style={{ fontSize: '1rem', fontWeight: 500 }}>
                      เมนูแนะนำ:
                </div>
                    <div style={{ fontSize: '1rem', fontWeight: 300, color: 'gray' }}>
                      {data.recommendedItems}
                    </div>

                  </div>


                </Col>

              </Row>

            </div>





          )

        } if (price === data.priceLevel) {
          return (
            <div style={{ background: "white", padding: 10, width: "100%", marginBottom: 10 }}>
              <Row gutter={{ xs: 12, sm: 8, md: 24, lg: 2 }}>

                <img src={data.coverImageId} alt="img" width="240px" height="240" />


                <Col offset={1}>

                  <div style={{ display: 'inline-flex' }}>
                    <div style={{ fontSize: '1.06rem', fontWeight: 500 }}>
                      {data.shopNameTH}

                    </div>
                    <div style={{ fontSize: '0.8rem', fontWeight: 300, background: 'green', color: 'white', marginLeft: 8, padding: '4px 10px 4px 10px', borderRadius: 10 }}>
                      {data.isOpen === "Y" ? "เปิด" : data.isOpen}
                    </div>
                  </div>
                  <div style={{ fontSize: '1.06rem', fontWeight: 300, color: 'grey' }}>
                    {data.categoryName}
                  </div>


                  <div style={{ fontSize: '1rem', fontWeight: 300 }}>
                    {data.subcategoryName} | ช่วงราคา    {data.priceLevel} |      {data.addressProvinceName} | {data.addressDistrictName}
                  </div>
                  <Divider />
                  <div style={{ fontSize: '1rem', fontWeight: 300 }}>
                    {data.facilities}
                  </div>




                  <div style={{ fontSize: '1rem', fontWeight: 300 }}>
                    <div dangerouslySetInnerHTML={{ __html: data.highlightText }}></div>

                  </div>
                  <div style={{ display: 'inline-flex', alignItems: 'center' }}>
                    <div style={{ fontSize: '1rem', fontWeight: 500 }}>
                      เมนูแนะนำ:
              </div>
                    <div style={{ fontSize: '1rem', fontWeight: 300, color: 'gray' }}>
                      {data.recommendedItems}
                    </div>

                  </div>


                </Col>

              </Row>

            </div>
          )
        }





      }
    }
