);
    setData(result.data);

if (result.status === 200) {
    setStatus(true)
      }

console.log(data.merchants)

  }*/}

  return (

      <div className="site-layout-background" style={{ padding: 8, minHeight: 2080 }}>
      <div style={{ color: "black", fontWeight: 600, fontSize: '24px' }}>ผลการค้นหา ทั้งหมด</div>

      {data.map((data, index) => 


        <>


        <Row gutter={{ xs: 8, sm: 16, md: 24, lg: 32 }}>
            {!isMobile &&
             <Col span={6} >
             <Form/>
                      </Col>
            } 
            {!isMobile  && 
                <Col span={18}  >
                <div style={{ background: "white", padding: 10, width: "100%" }}>
                  <Row gutter={{ xs: 12, sm: 8, md: 24, lg: 2 }}>

                    <img src={data.coverImageId} alt="img" width="240px" height="240" />


                  <Col offset={1}>


                  <div style={{fontSize:'1.06rem',fontWeight:500}}>
                  {data.shopNameTH}
                    </div>


             <div style={{fontSize:'1.06rem',fontWeight:300,color:'grey'}}>
             {data.categoryName}
             </div>


                  <div style={{fontSize:'1rem',fontWeight:300}}>
                  {data.subcategoryName}
                    </div>

                    <div style={{fontSize:'1rem',fontWeight:300}}>
                  {data.facilities}
                    </div>


                    <div style={{fontSize:'1rem',fontWeight:300}}>
                  {data.priceLevel}
                    </div>

                    </Col>

                  </Row>
                </div>
              </Col>


            }

  </Row>
          {isMobile &&
            <div style={{ background: "white", width: "100%" ,display:"block"}}> 
            <img src={img} alt="img" width="100%"  />
            <div style={{fontSize:'1.06rem',fontWeight:500}}>
                  {data.shopNameTH}
             </div>
              </div>
           }
        </>





      )}

      </div>

  );
}
